void zte_disp_handle_lp_event(struct dsi_panel *panel, int power_mode)
{
	u64 jiffies_time = 0;
	u64 diff_time = 0;

	if (!panel) {
		pr_err("invalid params\n");
		return;
	}

	if (panel->aod_exit_delay_time) {
		switch (power_mode) {
		case SDE_MODE_DPMS_LP1:
			if ((panel->power_mode != SDE_MODE_DPMS_LP1) &&
				(panel->power_mode != SDE_MODE_DPMS_LP2))
				panel->aod_enter_time = get_jiffies_64();
			break;
		case SDE_MODE_DPMS_ON:
			jiffies_time = get_jiffies_64();
			if (time_after64(jiffies_time, panel->aod_enter_time))
				diff_time = jiffies_time - panel->aod_enter_time;
			diff_time = (diff_time * 1000)/ HZ;
			if (diff_time < panel->aod_exit_delay_time
				&& panel->aod_exit_delay_time < MAX_SLEEP_TIME){
				   pr_info("MSM_LCD aod_exit delay %d ms\n", panel->aod_exit_delay_time);
                   msleep(panel->aod_exit_delay_time);
				}
			break;
		case SDE_MODE_DPMS_OFF:
			panel->aod_enter_time = 0;
			break;
		default:
			break;
		}
	}
}

int zte_convert_backlevel_function(int level, u32 bl_max, u32 mode)
{
	int bl, convert_level;

	if (level == 0)
		return 0;

	bl = level * 255 / bl_max;

	if (!bl && level)
		bl = 1;/*ensure greater than 0 and less than 16 equal to 1*/

	switch (mode) {
	case CURVE_MATRIX_MAX_350_LUX:
		convert_level = zte_backlight_curve_matrix_max_350_lux[bl];
		break;
	case CURVE_MATRIX_MAX_400_LUX:
		convert_level = zte_backlight_curve_matrix_max_400_lux[bl];
		break;
	case CURVE_MATRIX_MAX_450_LUX:
		convert_level = zte_backlight_curve_matrix_max_450_lux[bl];
		break;
	case CURVE_MATRIX_MAX_RM692C9_LUX:
		convert_level = zte_backlight_curve_matrix_max_rm692c9_lux[bl];
		break;
	default:
		convert_level = zte_backlight_curve_matrix_max_450_lux[bl];
		break;
	}

	convert_level = convert_level * bl_max / 255;

	return convert_level;
}
